import 'package:flutter/material.dart';

void main() {
  runApp(const MeuApp());
}

class MeuApp extends StatelessWidget {
  const MeuApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: const GridPage(),
      debugShowCheckedModeBanner: false,
    );
  }
}

class GridPage extends StatelessWidget {
  const GridPage({super.key});

  final List<Map<String, String>> lugares = const [
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Flora',
      'subtitulo': 'Fada da natureza',
    },
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Musa',
      'subtitulo': 'Fada da música',
    },
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Bloom',
      'subtitulo': 'Fada da chama do dragão',
    },
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Estela',
      'subtitulo': 'Fada do sol e da lua',
    },
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Tecna',
      'subtitulo': 'Fada da tecnologia',
    },
    {
      'imagem': 'data:image/jpeg;base64,',
      'titulo': 'Aisha',
      'subtitulo': 'Fada das ondas',
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Padding(
        padding: const EdgeInsets.all(8.0),
        child: GridView.count(
          crossAxisCount: 2,
          crossAxisSpacing: 10,
          mainAxisSpacing: 10,
          children: lugares.map((lugar) {
            return AspectRatio(
              aspectRatio: 1, 
              child: ClipRRect(
                child: Stack(
                  fit: StackFit.expand,
                  children: [
                    Image.network(
                      lugar['imagem']!,
                      fit: BoxFit.cover,
                    ),
                    Positioned(
                      bottom: 0,
                      left: 0,
                      right: 0,
                      child: Container(
                        padding: const EdgeInsets.all(8),
                        color: Colors.black.withOpacity(0.6),
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              lugar['titulo']!,
                              style: const TextStyle(
                                color: Colors.white,
                                fontWeight: FontWeight.bold,
                                fontSize: 14,
                              ),
                            ),
                            Text(
                              lugar['subtitulo']!,
                              style: const TextStyle(
                                color: Colors.white70,
                                fontSize: 12,
                              ),
                            ),
                          ],
                        ),
                      ),
                    ),
                  ],
                ),
              ),
            );
          }).toList(),
        ),
      ),
    );
  }
}
